library(lubridate)
library(hms)
library(dplyr)
# ===================================
# ====== define all variables =======
# ===================================

days_duration_study = 14
surveys_by_day      = 4
timezone            = "CET"
to_day              = today(timezone)
now_date            = now(timezone)
t_zero              = "00:05"
last_timepoint      = "23:30"
timepoints          = c("09:00", "13:00","17:00","21:00")  # write just the hours
ESM_questionnaire   = EF_RUM_ESM_v2_EN

# ===================================
# ===================================

to_day = today(timezone)
now_date = now(timezone)
t_end = force_tz(to_day + hm(last_timepoint), tzone = timezone)

t = c()
# create as many timepoints as needed
for(i in 1:length(timepoints)){
  t[i] = as.character(force_tz(to_day + hm(timepoints[i])), tzone = timezone)
}

# create the interval list
intervals_list = list()
for(i in 1:length(t)){
  if(i == length(t)){
    intervals_list = c(intervals_list, list(interval(ymd_hms(t[i], tz = timezone),ymd_hms(t_end,  tz = timezone))))
  } else {
    intervals_list = c(intervals_list, list(interval(ymd_hms(t[i], tz = timezone),ymd_hms(t[i+1],  tz = timezone))))

  }
}

# ===================================================================
# 1) get the dates of ESM_questionnaire$created with dplyr::pull(aframe, a2)


if(nrow(ESM_questionnaire) > 0 ){
  entries_of_today = force_tz(ymd_hms(ESM_questionnaire %>% select("ended") %>% filter(ended == today()) %>% pull(ended)), tzone = timezone) 
} else {
  entries_of_today = NULL
}


# depending on the time check whether the questionnaires have been skipped or not.
now()

e = 0
progress = ""
text = ""
# == T0 == si on il n'est pas encore l'heure, attendre
for(i in 1:length(t)){
  if(now_date < t[i] ){
    # if here, it means that it hasn't been answered yet
      progress = paste0(progress, "\n#### <span style='color:darkgray'>**",format(strptime(t[i],"%Y-%m-%d %H:%M:%S"), "%H:%M"),"**</span> ![](https://i.imgur.com/xuosH6S.png){width=40px}\n")
    
    if( i == e ){
        
        text = paste0("#### Thank you very much for your answers. You will receive your next questionnaire at ", format(strptime(t[i],"%Y-%m-%d %H:%M:%S"), "%H:%M"),".\n\nSee you later." )
        print(text) 
      } 
      
    # if it's the time of the day
      if( i == 1 ){
        text = paste0("Hello, it is still too early!\n\nYou will receive your next questionnaire at ", format(strptime(t[i],"%Y-%m-%d %H:%M:%S"), "%H:%M"),". \n\nSee you later." )
        print(text)
      } 
  } else {
    e = i+1
    
    # so the time point has passed
    # test whether the participant answered the survey
    
    if(!is.null(entries_of_today)){
      if(any(entries_of_today %within% intervals_list[i])) {
        # if TRUE, SHOW GREEN PICTURE
        progress = paste0(progress, "\n#### <span style='color:green'>**", format(strptime(t[i],"%Y-%m-%d %H:%M:%S"), "%H:%M"),"**</span> ![](https://i.imgur.com/nUY7Ifu.png){width=40px}\n")
      } else {
        # if FALSE, SHOW RED PICTURE
        progress = paste0(progress, "\n#### <span style='color:red'>**", format(strptime(t[i],"%Y-%m-%d %H:%M:%S"), "%H:%M"),"**</span> ![](https://i.imgur.com/d8RGdld.png){width=40px}\n")
      }
    } else {
      # if FALSE, SHOW RED PICTURE
      progress = paste0(progress, "\n#### <span style='color:red'>**", format(strptime(t[i],"%Y-%m-%d %H:%M:%S"), "%H:%M"),"**</span> ![](https://i.imgur.com/d8RGdld.png){width=40px}\n")
    }
  }
}

